import ExpencesForm from "./ExpencesForm";
import ListExpences from "./ListExpences";
import { useState } from "react";

const AddExpences = () => {
  const [data, setData] = useState([
    {
      id: Math.random().toString(),
      title: "title",
      amount: 500,
      date: new Date("2020, 2, 20"),
    },
  ]);
  const saveData = (expData) => {
    const expenData = {
      ...expData,
    };
    console.log(expenData);
    setData(...expenData, data);
  };
  return (
    <div>
      <ExpencesForm onSaveData={saveData} />
      <div>
        <ListExpences items={data}></ListExpences>
      </div>
    </div>
  );
};
export default AddExpences;
